	public Song2Script()
	{
		music = GameManager.Instance.GetComponent<AudioSource>();

		//they're the same song now, but you could add a different song here
		song = Resources.Load<AudioClip>("Audio/song1Test");

		//load the correct song by detecting what button was pressed
		GameManager.Instance.song = 2;

		//set the song information
		music.clip = song;
		music.volume = volume;

		//set the correct volume
		if (OptionScript.Volume != null)
		{
			volume = OptionScript.Volume.value;
		}

		//start the song at the correct time
		music.Play();
		music.time = 9f;
	}

	public void Unload()
	{
		//reset all the numbers, unload the song, stop the music
		NoteControlScript.hit = 0;
		NoteControlScript.miss = 0;
		NoteControlScript.combo = 0;
		music.Stop();

		//make sure the intro doesn't play again
		GameManager.Instance.intro = false;
		GameManager.Instance.song = 0;

		Destroy(this);
	}
}
